import { StyledImg, StyledButton, CardsContainer } from "./style";
import { BASE_URL } from "../Constants/BASE_URL";
import React, { useEffect, useState } from "react";
import { getCards } from "../Service/Request";

export default function Home() {
    const [cards, setCards] = useState({})
    const [trigger, setTrigger] = useState(false)
    const [selectedCard, setSelectedCard] = useState({})
    const [shuffled, setShuffled] = useState(false)

    useEffect(() => {
        getCards(cards,setCards)
    // eslint-disable-next-line react-hooks/exhaustive-deps
    },[])

    let copyArray = cards.length > 0 && [...cards]

    const shuffleCards = (array) => {
        setShuffled(true)
        let newPosition
        let temp

        for (let card of array) {
            card.flipCard = false
        }

        for (let i = array.length -1; i > 0; i--) {
            newPosition = Math.floor(Math.random() * i);
            temp = array[i]
            array[i] = array[newPosition]
            array[newPosition] = temp
        }
        setCards(array)
    }

    const popupCard = (card) => {
        setTrigger(true)
        card.flipCard = true
        setSelectedCard(card)
    }

    const restartCards = () => {
        setShuffled(false)
        getCards(cards, setCards)
    }

    const cardsMap = cards.length > 0 && cards.map((card) => {
        return (
            <StyledImg
            src={`${BASE_URL}/${card.image}`}
            alt={card.name}
            key={card.name}
            />
        )
    })

    return (
        <div>
            <div>
                <StyledButton
                onClick={restartCards}>
                    Recome√ßar
                </StyledButton>
                <StyledButton 
                onClick={() => shuffleCards(copyArray)}>
                    Embaralhar
                </StyledButton>
            </div>
            <CardsContainer>
                {cardsMap}
            </CardsContainer>
        </div>
    )
}
